import React from 'react';
import { ConversationalForm } from 'conversational-form';

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.formFields = [

      // Question 1
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_colour',
        'cf-questions': 'What is the natural color of your hair?',
        'cf-label': 'Black',
        'cf-image': 'https://edenhairextensions.com.au/wp-content/uploads/2017/06/products-1_JET_BLACK_06d870da-50ae-4000-acb1-0bdd64cb33ea.jpg'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_colour',
        'cf-questions': 'What is the natural color of your hair?',
        'cf-label': 'Brown',
        'cf-image': 'https://edenhairextensions.com.au/wp-content/uploads/2017/06/products-6CHESTNUT_fefc6912-da6a-433d-9a13-44a943c726ef.jpg'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_colour',
        'cf-questions': 'What is the natural color of your hair?',
        'cf-label': 'Blonde',
        'cf-image': 'https://edenhairextensions.com.au/wp-content/uploads/2017/06/products-613_BLEACHED_BLONDE_f1bce279-c1bc-465e-b0a3-b2b7258def5b.jpg'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_colour',
        'cf-questions': 'What is the natural color of your hair?',
        'cf-label': 'Auburn',
        'cf-image': 'https://www.clairol.com/m/master/products/new_swatches/00070018116758_m.png'
      },

      // Question 2
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_texture',
        'cf-questions': 'What is the natural texture of your hair?',
        'cf-label': 'Wavy'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_texture',
        'cf-questions': 'What is the natural texture of your hair?',
        'cf-label': 'Straight'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'hair_texture',
        'cf-questions': 'What is the natural texture of your hair?',
        'cf-label': 'Coilly'
      },

      // Question 3
      {
        'tag': 'input',
        'type': 'checkbox',
        'name': 'hair_problems',
        'cf-questions': 'Have you noticed any problems with your hair?',
        'cf-label': 'Wavy'
      },
      {
        'tag': 'input',
        'type': 'checkbox',
        'name': 'hair_problems',
        'cf-questions': 'Have you noticed any problems with your hair?',
        'cf-label': 'Straight'
      },
      {
        'tag': 'input',
        'type': 'checkbox',
        'name': 'hair_problems',
        'cf-questions': 'Have you noticed any problems with your hair?',
        'cf-label': 'Coilly'
      },

      //personal_hair_ques,

      //How much do you spend on a visit

      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '<100',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '100-200',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '200-500',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '>500',
      },
    ];

    this.submitCallback = this.submitCallback.bind(this);
  }

  componentDidMount() {
    this.cf = ConversationalForm.startTheConversation({
      options: {
	      theme: 'dark',
        submitCallback: this.submitCallback,
        preventAutoFocus: true,
        showProgressBar: true
        // loadExternalStyleSheet: false
      },
      tags: this.formFields
    });
    this.elem.appendChild(this.cf.el);
  }

  submitCallback() {
    var formDataSerialized = this.cf.getFormData(true);
    console.log("Formdata, obj:", formDataSerialized);
    this.cf.addRobotChatResponse("You are done. Check the dev console for form data output.")
  }

  render() {
    return (
      <div>
        <div
          ref={ref => this.elem = ref}
        />
      </div>
    );
  }
}
