import React from 'react';
import { ConversationalForm } from 'conversational-form';

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.formFields = [
      {
        'tag': 'input',
        'type': 'text',
        'name': 'firstname',
        'cf-questions': 'What is your firstname?'
      },
      {
        'tag': 'input',
        'type': 'text',
        'name': 'lastname',
        'cf-questions': 'What is your lastname?'
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'frequency',
        'cf-questions': 'How frequently do you visit a salon?',
        'cf-label': 'Weekly',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'frequency',
        'cf-questions': 'How frequently do you visit a salon?',
        'cf-label': 'Montly',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'frequency',
        'cf-questions': 'How frequently do you visit a salon?',
        'cf-label': 'Bi-Montly',
      },

      //How much do you spend on a visit

      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '<100',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '100-200',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '200-500',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '>500',
      },

      //How fair is your salon ?

      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '<100',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '100-200',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '200-500',
      },
      {
        'tag': 'input',
        'type': 'radio',
        'name': 'visit_spend',
        'cf-questions': 'How much do you roughly spend on a visit?',
        'cf-label': '>500',
      },
    ];

    this.submitCallback = this.submitCallback.bind(this);
  }

  componentDidMount() {
    this.cf = ConversationalForm.startTheConversation({
      options: {
	      theme: 'dark',
        submitCallback: this.submitCallback,
        preventAutoFocus: true,
        // loadExternalStyleSheet: false
      },
      tags: this.formFields
    });
    this.elem.appendChild(this.cf.el);
  }

  submitCallback() {
    var formDataSerialized = this.cf.getFormData(true);
    console.log("Formdata, obj:", formDataSerialized);
    this.cf.addRobotChatResponse("You are done. Check the dev console for form data output.")
  }

  render() {
    return (
      <div>
        <div style={{borderRadius : "25px"}}
          ref={ref => this.elem = ref}
        />
      </div>
    );
  }
}
